mapper.select:
SELECT cart_id
FROM cart
WHERE email = #{email}

mapper.update:
UPDATE cart
SET cartStock1 = +1
WHERE email = #{email}

UPDATE cart
SET cartStock2 = +1
WHERE email = #{email}

UPDATE cart
SET cartStock3 = +1
WHERE email = #{email}

UPDATE cart
SET cartStock4 = +1
WHERE email = #{email}

UPDATE cart
SET cartStock5 = +1
WHERE email = #{email}

UPDATE cart
SET cartStock6 = +1
WHERE email = #{email}

mapper.select:

SELECT SUM(cartStock1 + cartStock2 + cartStock3 + cartStock4 + cartStock5 + cartStock6)
FROM cart
WHERE email = #{email}
